-- Amazon Selling Partner Item Inventory table creation query
-- Contains inventory data from Amazon Selling Partner API
CREATE OR REPLACE TABLE `sheets_base_schema.amazon_selling_partner__item_inventory` (
  `source_relation` STRING OPTIONS(description = 'The `<database>`.`<schema>` this record came from.'),
  `marketplace_id` STRING OPTIONS(description = 'The unobfuscated marketplace identifier. Different marketplace values can be found [here](https://developer-docs.amazon.com/sp-api/docs/marketplace-ids).'),
  `asin` STRING OPTIONS(description = 'The item\'s Amazon Standard Identification Number (ASIN).'),
  `item_name` STRING OPTIONS(description = 'Name, or title, associated with an Amazon catalog item.'),
  `display_name` STRING OPTIONS(description = 'Display name for the classification.'),
  `product_name` STRING OPTIONS(description = 'The localized language product title of the item within the specific marketplace.'),
  `brand` STRING OPTIONS(description = 'Name of the brand associated with an Amazon catalog item.'),
  `color` STRING OPTIONS(description = 'Name of the color associated with an Amazon catalog item.'),
  `size` STRING OPTIONS(description = 'Name of the size associated with an Amazon catalog item.'),
  `style` STRING OPTIONS(description = 'Name of the style associated with an Amazon catalog item.'),
  `package_quantity` INT64 OPTIONS(description = 'Quantity of an Amazon catalog item in one package.'),
  `manufacturer` STRING OPTIONS(description = 'Name of the manufacturer associated with an Amazon catalog item.'),
  `contributors` INT64 OPTIONS(description = 'Individual contributors to the creation of an item, such as the authors or actors.'),
  `product_type` STRING OPTIONS(description = 'Name of the product type associated with the Amazon catalog item.'),
  `condition` STRING OPTIONS(description = 'The condition of the item as described by the seller (for example, New Item).'),
  `release_date` DATE OPTIONS(description = 'First date on which an Amazon catalog item is shippable to customers.'),
  `item_classification` STRING OPTIONS(description = 'Classification type associated with the Amazon catalog item. Possible values can be found [here](https://developer-docs.amazon.com/sp-api/docs/catalog-items-api-v2022-04-01-reference#itemclassification).'),
  `classification_id` INT64 OPTIONS(description = 'Identifier of the classification associated with the sales rank.'),
  `classification_sales_rank_link` STRING OPTIONS(description = 'Corresponding Amazon retail website link, or URL, for the sales rank.'),
  `classification_sales_rank` INT64 OPTIONS(description = 'Sales rank value.'),
  `website_display_group` STRING OPTIONS(description = 'Identifier of the website display group associated with an Amazon catalog item.'),
  `website_display_group_name` STRING OPTIONS(description = 'Display name of the website display group associated with an Amazon catalog item.'),
  `website_display_group_sales_rank_link` STRING OPTIONS(description = 'Corresponding Amazon retail website link, or URL, for the sales rank.'),
  `website_display_group_sales_rank` INT64 OPTIONS(description = 'Sales rank value.'),
  `is_memorabilia` BOOL OPTIONS(description = 'Identifies an Amazon catalog item is memorabilia valued for its connection with historical events, culture, or entertainment.'),
  `is_adult_product` BOOL OPTIONS(description = 'Identifies an Amazon catalog item is intended for an adult audience or is sexual in nature.'),
  `is_autographed` BOOL OPTIONS(description = 'Identifies an Amazon catalog item is autographed by a player or celebrity.'),
  `is_trade_in_eligible` BOOL OPTIONS(description = 'Identifies an Amazon catalog item is eligible for trade-in.'),
  `model_number` STRING OPTIONS(description = 'Model number associated with an Amazon catalog item.'),
  `part_number` STRING OPTIONS(description = 'Part number associated with an Amazon catalog item.'),
  `parent_variation_asin` STRING OPTIONS(description = 'Identifier (ASIN) of the item\'s variation parent.'),
  `parent_package_container_asin` STRING OPTIONS(description = 'Identifier (ASIN) of the item\'s package container parent.'),
  `sku` STRING OPTIONS(description = 'Stock Keeping Unit, a seller-specified identifier for an Amazon listed item.'),
  `fn_sku` STRING OPTIONS(description = 'Amazon\'s fulfillment network SKU identifier.'),
  `ean` STRING OPTIONS(description = 'European Article Number of the catalog item.'),
  `gtin` STRING OPTIONS(description = 'Global Trade Item Number of the catalog item.'),
  `isbn` STRING OPTIONS(description = 'International Standard Book Number of the catalog item.'),
  `jan` STRING OPTIONS(description = 'Japanese Article Number of the catalog item.'),
  `minsan` STRING OPTIONS(description = 'Minsan Code of the catalog item.'),
  `upc` STRING OPTIONS(description = 'Universal Product Code of the catalog item.'),
  `count_images` INT64 OPTIONS(description = 'Count of images for an item in the Amazon catalog for the indicated Amazon marketplace.'),
  `count_swatch_images` INT64 OPTIONS(description = 'Count of Swatch images for this item.'),
  `item_height_unit` STRING OPTIONS(description = 'Measurement unit of the dimension value.'),
  `item_height_value` INT64 OPTIONS(description = 'Height of an item.'),
  `item_length_unit` STRING OPTIONS(description = 'Measurement unit of the dimension value.'),
  `item_length_value` INT64 OPTIONS(description = 'Length of an item.'),
  `item_weight_unit` STRING OPTIONS(description = 'Measurement unit of the dimension value.'),
  `item_weight_value` INT64 OPTIONS(description = 'Weight of an item.'),
  `item_width_unit` STRING OPTIONS(description = 'Measurement unit of the dimension value.'),
  `item_width_value` INT64 OPTIONS(description = 'Width of an item.'),
  `package_height_unit` STRING OPTIONS(description = 'Measurement unit of the dimension value.'),
  `package_height_value` INT64 OPTIONS(description = 'Height of an item package.'),
  `package_length_unit` STRING OPTIONS(description = 'Measurement unit of the dimension value.'),
  `package_length_value` INT64 OPTIONS(description = 'Length of an item package.'),
  `package_weight_unit` STRING OPTIONS(description = 'Measurement unit of the dimension value.'),
  `package_weight_value` INT64 OPTIONS(description = 'Weight of an item package.'),
  `package_width_unit` STRING OPTIONS(description = 'Measurement unit of the dimension value.'),
  `package_width_value` INT64 OPTIONS(description = 'Width of an item package.'),
  `inventory_summary_id` STRING OPTIONS(description = 'Fivetran-generated Primary Key for inventory summaries.'),
  `inventory_last_updated_at` DATETIME OPTIONS(description = 'The date and time that any quantity was last updated.'),
  `total_quantity` INT64 OPTIONS(description = 'The total number of units in an inbound shipment or in Amazon fulfillment centers.'),
  `total_researching_quantity` INT64 OPTIONS(description = 'The total number of units in Amazon\'s fulfillment network that are currently being picked, packed, and shipped; or are sidelined for measurement, sampling, or other internal processes.'),
  `total_reserved_quantity` INT64 OPTIONS(description = 'The total number of units in Amazon\'s fulfillment network that are currently being picked, packed, and shipped; or are sidelined for measurement, sampling, or other internal processes.'),
  `fullfillable_quantity` INT64 OPTIONS(description = 'The item quantity that can be picked, packed, and shipped.'),
  `total_unfulfillable_quantity` INT64 OPTIONS(description = 'The total number of units in Amazon\'s fulfillment network in unsellable condition.'),
  `pending_customer_order_quantity` INT64 OPTIONS(description = 'The number of units reserved for customer orders.'),
  `pending_transshipment_quantity` INT64 OPTIONS(description = 'The number of units being transferred from one fulfillment center to another.'),
  `fc_processing_quantity` INT64 OPTIONS(description = 'The number of units that have been sidelined at the fulfillment center for additional processing.'),
  `inblound_shipped_quantity` INT64 OPTIONS(description = 'The number of units in an inbound shipment that you have notified Amazon about and have provided a tracking number.'),
  `inbound_receiving_quantity` INT64 OPTIONS(description = 'The number of units that have not yet been received at an Amazon fulfillment center for processing, but are part of an inbound shipment with some units that have already been received and processed.'),
  `inbound_working_quantity` INT64 OPTIONS(description = 'The number of units in an inbound shipment for which you have notified Amazon.'),
  `warehouse_damaged_quantity` INT64 OPTIONS(description = 'The number of units in warehouse damaged disposition.'),
  `carrier_damaged_quantity` INT64 OPTIONS(description = 'The number of units in carrier damaged disposition.'),
  `customer_damaged_quantity` INT64 OPTIONS(description = 'The number of units in customer damaged disposition.'),
  `defective_quantity` INT64 OPTIONS(description = 'The number of units in defective disposition.'),
  `distributor_damaged_quantity` INT64 OPTIONS(description = 'The number of units in distributor damaged disposition.'),
  `expired_quantity` INT64 OPTIONS(description = 'The number of units in expired disposition.'),
  `short_term_research_quantity` INT64 OPTIONS(description = 'The quantity of misplaced or warehouse damaged inventory that is actively being confirmed at Amazon fulfillment center and researched for a duration of 1-10 days.'),
  `mid_term_research_quantity` INT64 OPTIONS(description = 'The quantity of misplaced or warehouse damaged inventory that is actively being confirmed at Amazon fulfillment center and researched for a duration of 11-20 days.'),
  `long_term_research_quantity` INT64 OPTIONS(description = 'The quantity of misplaced or warehouse damaged inventory that is actively being confirmed at Amazon fulfillment center and researched for a duration of 21 days or longer.')
)
OPTIONS (description = 'Inventory data from the Amazon Selling Partner API');
